import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import { NgNavigatorShareService } from 'ng-navigator-share';
import { AdultsService } from '../../adults.service';
import { PartnershipReport } from '../partnership-report.model';
import { Location } from '@angular/common';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-tree-plantation-report',
  templateUrl: './tree-plantation-report.page.html',
  styleUrls: ['./tree-plantation-report.page.scss'],
})
export class TreePlantationReportPage implements OnInit {

  partnershipReport:any;
  groupedDates = [];
  currentDate=new Date();
  years:any=[];
  totalSubscriber:number;
  selectedYear=0;
  totalPartners:number;
  totalRevenu:number;
  iframeSrc:SafeResourceUrl;
  isPdfDownloading=false;
  isCopy=true;
  value='data:image/svg+xml;base64,'
 
 constructor(  private sanitizer: DomSanitizer,public adultService:AdultsService, private ngNavigatorShareService: NgNavigatorShareService,public router:Router,private location:Location) { 
 this.iframeSrc=this.sanitizer.bypassSecurityTrustUrl(this.value);
}

 ngOnInit() {
   this.adultService.getTreePlantationReport().subscribe(res=>{
     if(res){
       this.partnershipReport=res;
       
     }
   })
 }
 
 getLoop(tree){
  var array=[]
  for(let i=1;i<=+tree;i++){
    array.push(i);
  }
  return array;
 }

 redirectToMyPartnership(){
  this.router.navigate(['adults/partnership-report/my-partner'])
}
 DownloadPdf(){
  this.isPdfDownloading=true;
  setTimeout(() => {
    let DATA: any = document.getElementById("partnershipReport");
    html2canvas(DATA).then((canvas) => {

      const imgData = canvas.toDataURL("image/jpeg") 
      const pdf = new jsPDF("p","mm","a5");
      const imageProps = pdf.getImageProperties(imgData)
      const pdfw = pdf.internal.pageSize.getWidth()
      const test = pdf.internal.pageSize.getHeight()
      const pdfh = (imageProps.height * pdfw) / imageProps.width
      pdf.addImage(imgData, 'PNG', 0, 0, pdfw, test)
      pdf.save("tree-plantation-report.pdf");
    
    });
    this.isPdfDownloading=false;
  }, 500);
 }
 copyText(referralCode): void {
  navigator.clipboard.writeText(referralCode).catch(() => {
    console.error("Unable to copy text");
  });
  this.isCopy=false;
  setTimeout(() => {
    this.isCopy=true;
  }, 4000);
}
 share(refcode){
   this.ngNavigatorShareService.share({
     title: 'HumanWisdom Program',
     text:  "Hi! I’ve just subscribed to the amazing HumanWisdom app and joined their partnership program to help share this with others and make the world a better place. The app is free to download and browse. This is a short video introduction: https://youtu.be/GYbpYnkGJ0U. If you like it and want to subscribe use this referral code to get 10% off – "+refcode+". If you want to find out more about the partnership program – https://humanwisdom.me/adults/partnership-webpage"
   }).then( (response) => {
     console.log(response);
   })
   .catch( (error) => {
     console.log(error);
   });
 }

  goBack()
  {
  this.router.navigate(['adults/adult-dashboard'])
  }

}
